package integer_programs;

public class Integer_Operations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Integer a = 10;
		Integer b = 10;
		Integer c = 600;
		Integer d = 600;
		
		if(a==b)
		{
			System.out.println("a=b");
		}
		else
		{
			System.out.println("a!=b");
		}
		if(c==d)
		{
			System.out.println("c=d");
		}
		else
		{
			System.out.println("c!=d");
			//Integer wrapper class stores values in the memory from 
			//-128 to 128, so when it exceed three char it then it refers to same
		}
		
		
		

	}

}
